import React, { useState, useEffect,useContext,} from "react";
import { StyleSheet, Text,Image, View,Button,TouchableOpacity,Switch,ActivityIndicator,Dimensions,Modal} from 'react-native';
import{StudentContext} from "../ContextApi";
import MapView,{Marker,Polyline} from 'react-native-maps';
import * as Location from "expo-location";
import { Ionicons } from '@expo/vector-icons';
import variable from "../localhost";
const {width, height} = Dimensions.get('window');


export function TrackBus({ navigation,route }) {
const Sdata=useContext(StudentContext);
const[data,setData]=useState(Sdata);
const[stops,setStops]=useState();
const[initial,setInitial]=useState();
const[marker,setMarker]=useState({latitude:33.71398971857024, longitude:73.08249838549538})
const {width, height} = Dimensions.get('window')
const SCREEN_HEIGHT = height
const SCREEN_WIDTH = width
const ASPECT_RATIO = width / height
const LATITUDE_DELTA = 0.1922
const LONGITUDE_DELTA = LATITUDE_DELTA * ASPECT_RATIO;
const [modalVisible, setModalVisible] = useState(false);
const [modalText, setModalText] = useState();
const coordinates=[{latitude:33.713996572035036,longitude: 73.08284265417677},
                   {latitude:33.714855385416364,longitude: 73.08474071221934},
                   {latitude:33.70789410582329,longitude:  73.08994419772964},
                   {latitude:33.71439131757582,longitude: 73.1023467322258},
                   {latitude:33.71274921158932,longitude: 73.10354836185778},
                   {latitude:33.71153338989628,longitude:73.1035063923923},
                   {latitude:33.69841913110137,longitude:73.11039329853412},
                   {latitude:33.68937656454934,longitude:73.11041836965084},
                   {latitude:33.68908197295216 ,longitude:73.11092262494283},
                   {latitude:33.68923373238591, longitude: 73.11510687098277},
                   {latitude:33.688260681932405 ,longitude:73.11874394642835},
                   {latitude:33.687398125628306, longitude:73.13166068776665},
                   {latitude:33.686725165218995, longitude:73.13348820426081},
                   {latitude:33.66380578718912, longitude:73.15283229566113},
                   {latitude:33.65581337198406,longitude: 73.15398028112362},
                   {latitude:33.654231775225476,longitude:73.15531461083755},
                   {latitude:33.65317883787651 ,longitude: 73.15757444118358},]
useEffect(()=>{
    navigation.setOptions({ headerRight:()=>( 
        <TouchableOpacity onPress={()=>navigation.goBack()}  >        
    <Ionicons style={{marginRight:10,marginTop:5}}name="arrow-back" size={28} color="black" />
    </TouchableOpacity>) });
    setInitial({latitude:33.68914800157013 , longitude:73.11465232214398,latitudeDelta:LATITUDE_DELTA,longitudeDelta:LONGITUDE_DELTA});
    
},[]);

const view=(<View>
    <MapView style={styles.map}
    initialRegion={initial}
    >
    <Marker
        coordinate={{latitude:33.66380578718912, longitude:73.15283229566113}}
        onPress={e=>setStops(e.nativeEvent.coordinate)}
        resizeMethod="contain"
        >
        <Image
    source={require('../assets/bus.png')}
    style={{width: 35, height: 35}}
  />
        </Marker>
    <Polyline coordinates={coordinates} strokeColor="#FfC329" strokeWidth={7}/>
    
    
    </MapView>
    </View>)
    return (
        <View style={styles.container}>
            {initial!==undefined&&marker!==undefined?view:<View style={styles.ActivityIndicator}><ActivityIndicator  size="large" color="black"/></View>}
            </View>
      
    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
    }, 
    Button:{
    position:'absolute', 
    marginTop: height-150, 
    backgroundColor: '#fff',
    width: '70%',
    alignSelf:'center',
    borderRadius: 5,
    shadowColor: '#FfC329',
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.5,
    shadowRadius: 5,
    elevation: 10,
    },
    modalView: {
        width:"90%",
        alignSelf:"center",
        marginTop: "100%",
        backgroundColor: "white",
        borderRadius: 20,
        borderWidth:1,
        borderColor:"#FfC329",
        padding: 35,
        alignItems: "center",
        shadowColor: "#FfC329",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
      },
      SButton:{
        alignItems: "center",
        marginTop:10,
        backgroundColor:"#293038",
        width:100,
        height:30,
        borderRadius:6,
        paddingTop:"1.5%",
      
      },
      ButtonText:{
        color: "#FfC329",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },
      modalText:{
        textAlign: 'center',
      color:"#293038",
      fontSize:20
      },
    image: {
        marginTop:"20%",
        width: 200,
        height: 200,
        shadowColor: "yellow",
      shadowOffset: { height: 2},
      shadowOpacity: 0.3,
      },
      ActivityIndicator:{
        flex: 1,
        justifyContent: "center",
        transform: [{ scaleX: 2.2 }, { scaleY: 2.2 }]
      },
      map: {
        width: Dimensions.get('window').width,
        height: Dimensions.get('window').height,
      },
    
  });
