      }
      return(
          <View  style={styles.container}>
          <TouchableOpacity 
          style={styles.optionContainer}
         onPress={()=>{
          navigation.navigate("Change Password");
         }} >
          <Text style={styles.option} >Change Password</Text>
          </TouchableOpacity>
          </View>
  
      )
      
      };
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: "center",
      paddingTop:"1%"
    }, 
    optionContainer:{
      flexDirection:"row",
      alignItems: "center",
      backgroundColor:"white",
    justifyContent: "space-between",
    borderBottomWidth:1,
    borderBottomColor:"#FfC329",
    padding:"4%",
    marginVertical:"1%",
    marginHorizontal:"1%",
    width:"98%",
    borderRadius:10,
    elevation:6
  },
  option:{
    color:"#293038",
    fontSize:16,
    fontWeight:"bold"
},
    
  });
